import 'package:flutter/material.dart';
import 'pulsa.dart'; // Import the PulsaPage
import 'package:flutter_image_slideshow/flutter_image_slideshow.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Layout Demo',
      theme: ThemeData(
        primarySwatch: Colors.orange,
      ),
      home: const ShoopepayPage(),
    );
  }
}

class ShoopepayPage extends StatelessWidget {
  const ShoopepayPage({super.key});

  // Function to navigate to PulsaPage
  void _navigateToPulsaPage(BuildContext context) {
    Navigator.push(
      context,
      MaterialPageRoute(builder: (context) => PulsaPage()), // Navigate to PulsaPage
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("sopipay"),
        backgroundColor: Colors.orange,
      ),
      body: SingleChildScrollView(
        child: Column(
          children: [
            // Top Section with ShopeePay balance and SPayLater
            Container(
              color: Colors.orangeAccent,
              padding: const EdgeInsets.all(20),
              child: Column(
                children: [
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: const [
                      Text(
                        "ShopeePay",
                        style: TextStyle(fontSize: 18, color: Colors.white),
                      ),
                      Text(
                        "SPayLater",
                        style: TextStyle(fontSize: 18, color: Colors.white),
                      ),
                    ],
                  ),
                  const SizedBox(height: 10),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: const [
                      Text(
                        "Rp 0",
                        style: TextStyle(fontSize: 24, color: Colors.white),
                      ),
                      ElevatedButton(
                        onPressed: null,
                        child: Text("Ajukan Ulang"),
                      )
                    ],
                  ),
                ],
              ),
            ),

            // Grid Menu Section
            Padding(
              padding: const EdgeInsets.all(16.0),
              child: GridView.count(
                crossAxisCount: 4,
                shrinkWrap: true,
                physics: const NeverScrollableScrollPhysics(),
                children: [
                  const MenuItem(icon: Icons.account_balance_wallet, label: "Isi Saldo"),
                  const MenuItem(icon: Icons.qr_code, label: "Kode Bayar"),
                  const MenuItem(icon: Icons.send, label: "Transfer"),
                  const MenuItem(icon: Icons.account_balance, label: "Transfer Bank"),
                  // Make the "Pulsa" icon a button
                  GestureDetector(
                    onTap: () => _navigateToPulsaPage(context), // On tap, navigate to PulsaPage
                    child: const MenuItem(icon: Icons.smartphone, label: "Pulsa"),
                  ),
                  const MenuItem(icon: Icons.payment, label: "SPayLater Limit Xtra"),
                  const MenuItem(icon: Icons.fastfood, label: "ShopeeFood"),
                  const MenuItem(icon: Icons.account_balance, label: "Virtual Account"),
                  const MenuItem(icon: Icons.play_circle_outline, label: "Google Play"),
                  const MenuItem(icon: Icons.more_horiz, label: "Lihat Semua"),
                ],
              ),
            ),

            // Scrollable Ad Slideshow Section
            Container(
              color: Colors.grey[300],
              padding: const EdgeInsets.all(16.0),
              child: Column(
                children: [
                  const Text(
                    "Promo dan Diskon",
                    style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold),
                  ),
                  const SizedBox(height: 16),
                  ImageSlideshow(
                    width: double.infinity,
                    height: 200,
                    initialPage: 0,
                    indicatorColor: Colors.orange,
                    indicatorBackgroundColor: Colors.grey,
                    children: [
                      Image.network(
                        'data:image/jpeg;base64,',
                        fit: BoxFit.cover,
                      ),
                      Image.network(
                        'data:image/jpeg;base64,',
                        fit: BoxFit.cover,
                      ),
                      Image.network(
                        'data:image/jpeg;base64,',
                        fit: BoxFit.cover,
                      ),
                    ],
                    onPageChanged: (value) {
                      print('Page changed: $value');
                    },
                    autoPlayInterval: 3000,
                    isLoop: true,
                  ),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}

class MenuItem extends StatelessWidget {
  final IconData icon;
  final String label;

  const MenuItem({Key? key, required this.icon, required this.label}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Column(
      mainAxisAlignment: MainAxisAlignment.center,
      children: [
        Icon(icon, size: 40, color: Colors.orange),
        const SizedBox(height: 8),
        Text(label, textAlign: TextAlign.center),
      ],
    );
  }
}
